import React from "react";
import ReactDOM from "react-dom";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {isLoginOpen: true, isRegisterOpen: false};
    }

    showReg () {
      this.setState({isRegisterOpen: true, isLoginOpen: false});
    }

    showLogin () {
      this.setState({isLoginOpen: true, isRegisterOpen: false});
    }

    render () {
      return(
        <div className="root-container"> 

            <div className="box-controller">
                <div className="controller" onClick={this.showLogin.bind(this)}>
                  Login
                </div>
                <div className="controller" onClick={this.showReg.bind(this)}>
                  Register
                </div>
            </div>

            <div className="box-container">
              {this.state.isLoginOpen && <LoginBox />}
              {this.state.isRegisterOpen && <RegisterBox />}

            </div>

        </div>
      );
    };


}

class LoginBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {};
    }

    loginButton(e){

    }

    render () {
        return(
        <div className="inner-container">
            <div className="header">
              SIGN IN
            </div>

            <div className="box">

                <div className="input-group">

                  <label htmlFor="email">Email</label>
                  <input type="text" name="email" className="login-input" placeholder="Your email address"/>
                </div>

                <div className="input-group">

                  <label htmlFor="pwd">Password</label>
                  <input type="text" name="pwd" className="login-input" placeholder="Password"/>
                </div>

            <button type="button" className="login-btn" onClick={this.loginButton.bind(this)}>Sign in</button>

            </div>


        </div>
        );
    }

}

class RegisterBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {};
    }

    registerButton(e){

    }

    render () {
        return(
        <div className="inner-container">
            <div className="header">
              REGISTER
            </div>
            <div className="box">

                <div className="input-group">

                  <label htmlFor="email">Email</label>
                  <input type="text" name="email" className="reg-input" placeholder="Your email address"/>
                </div>

                <div className="input-group">

                  <label htmlFor="pwd">Password</label>
                  <input type="text" name="pwd" className="reg-input" placeholder="Password"/>
                </div>

            <button type="button" className="reg-btn" onClick={this.registerButton.bind(this)}>Register Your Account</button>

            </div>


        </div>
        );
    }

}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
