import React from "react";
import ReactDOM from "react-dom";
import * as Constants from "./api";


class LoginBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          emailAcc: "",
          passCode: "",
          errors:[],
        };

        this.changeEmail = this.changeEmail.bind(this);
        this.changePass = this.changePass.bind(this);
    }

    changeEmail(event) {
      this.setState({emailAcc: event.target.value});
    }
    changePass(event) {
      this.setState({passCode: event.target.value});
    }
    submitLogin(e){
        if (this.state.emailAcc === ""){
            return this.showValidErr("emailAcc", "email cannot be empty!")
        }
        if (this.state.passCode === ""){

        }

      Constants.LoginAcc(this.state.emailAcc, this.state.passCode)
    }

    showValidErr (elem, msg) {
        this.setState((prevState) => ({errors: [...prevState.errors, {elem, msg} ]}));
    }

    clearValidErr (elem) {
        this.setState((prevState) => {
            let newArr = [];
            for(let err of prevState.errors){
                if(elem != err.elem){
                    newArr.push(err);
                }
            }
            return newArr;
        })
    }

    render () {
        return(
        <div className="inner-container">
            <div className="header">
              SIGN IN NOW
            </div>

            <div className="box">
                <div className="input-group">

                  <label htmlFor="email">Email</label>
                  <input type="email" name="email" id="email" className="login-input" placeholder="Your email address" value={this.state.emailAcc}
                  onChange={this.changeEmail}/>
                </div>

                <div className="input-group">

                  <label htmlFor="pwd">Password</label>
                  <input type="text" name="pwd" id="pwd" className="login-input" placeholder="Password" value={this.state.passCode}
                  onChange={this.changePass}/>
                </div>
            <button type="button" className="login-btn" onClick={this.submitLogin.bind(this)}>Sign in</button>

            </div>


        </div>
        );
    }

}

class RegisterBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          emailAcc:"",
          passCode:"",
        };

        this.changeEmail = this.changeEmail.bind(this);
        this.changePass = this.changePass.bind(this);
    }

    changeEmail(event) {
      this.setState({emailAcc: event.target.value});
    }

    changePass(event) {
      this.setState({passCode: event.target.value});
    }

    submitReg(e){
      Constants.RegAcc(this.state.emailAcc, this.state.passCode);
    }

    render () {
        return(
        <div className="inner-container">
            <div className="header">
              REGISTER ACCOUNT
            </div>
            <div className="box">

                <div className="input-group">

                  <label htmlFor="email">Email</label>
                  <input type="text" name="email" className="reg-input" placeholder="Your email address" value={this.state.emailAcc}
                    onChange={this.changeEmail}/>
                </div>

                <div className="input-group">

                  <label htmlFor="pwd">Password</label>
                  <input type="text" name="pwd" className="reg-input" placeholder="Password" value={this.state.passCode}
                    onChange={this.changePass}/>
                </div>

            <button type="button" className="reg-btn" onClick={this.submitReg.bind(this)}>Sign up</button>

            </div>


        </div>
        );
    }

}

export {LoginBox , RegisterBox}
