import React from "react";
import ReactDOM from "react-dom";
import LoginAcc from "./api";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {isLoginOpen: true, isRegisterOpen: false};
    }

    showReg () {
      this.setState({isRegisterOpen: true, isLoginOpen: false});
    }

    showLogin () {
      this.setState({isLoginOpen: true, isRegisterOpen: false});
    }

    render () {
      return(
        <div className="root-container"> 

            <div className="box-controller">
                <div className="controller" onClick={this.showLogin.bind(this)}>
                  Log In
                </div>
                <div className="controller" onClick={this.showReg.bind(this)}>
                  Sign up
                </div>
            </div>

            <div className="box-container">
              {this.state.isLoginOpen && <LoginBox />}
              {this.state.isRegisterOpen && <RegisterBox />}

            </div>

        </div>
      );
    };


}

class LoginBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          emailAcc: "",
          passCode: "",
        };

        this.changeEmail = this.changeEmail.bind(this);
        this.changePass = this.changePass.bind(this);
    }

    changeEmail(event) {
      this.setState({emailAcc: event.target.value});
    }

    changePass(event) {
      this.setState({passCode: event.target.value});
    }

    render () {
        return(
        <div className="inner-container">
            <div className="header">
              SIGN IN NOW
            </div>

            <div className="box">
                <div className="input-group">

                  <label htmlFor="email">Email</label>
                  <input type="text" name="email" id="email" className="login-input" placeholder="Your email address" value={this.state.emailAcc}
                  onChange={this.changeEmail}/>
                </div>

                <div className="input-group">

                  <label htmlFor="pwd">Password</label>
                  <input type="text" name="pwd" id="pwd" className="login-input" placeholder="Password" value={this.state.passCode}
                  onChange={this.changePass}/>
                </div>
            <button type="button" className="login-btn" onClick={LoginAcc(this.state.emailAcc, this.state.passCode)}>Sign in</button>

            </div>


        </div>
        );
    }

}

class RegisterBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {};
    }

    render () {
        return(
        <div className="inner-container">
            <div className="header">
              REGISTER ACCOUNT
            </div>
            <div className="box">

                <div className="input-group">

                  <label htmlFor="email">Email</label>
                  <input type="text" name="email" className="reg-input" placeholder="Your email address"/>
                </div>

                <div className="input-group">

                  <label htmlFor="pwd">Password</label>
                  <input type="text" name="pwd" className="reg-input" placeholder="Password"/>
                </div>

            <button type="button" className="reg-btn" onClick={RegAcc}>Sign up</button>

            </div>


        </div>
        );
    }

}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
