import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
import { SearchBar, useSearch } from "./search";

export function Main() {
    const { loading, types, error } = useOffsCategory();
    const [search, setsearch] = useState("")
    const { searching, offences, Serror } = useSearch(search);
    console.log(offences);    
    if (loading) {
        return <div className="loading"> Searching... </div>
    }

    if (error) {
        return <p>Something's wrong: {error.message}</p>
    }
    return (
        <div className="search-display">

            <div>
                <SearchBar onSubmit={setsearch} />

                {offences.map((offence) => (
                    <Displayingmulti key={offence} name={offence.LGA} number={offence.total} />
                ))}

                {types.map((type) => (
                    <Displaying key={type} name={type} />
                ))}
            </div>

        </div>
    );
}

function Displayingmulti(props) {
    return (
        <div>
            <p>{props.name}</p>
            <p>{props.number}</p>
        </div>
    );
}

function Displaying(props) {
    return (
        <div>
            <p>{props.name}</p>
        </div>
    );
}

function OpenSource() {
    const url = "https://cab230.hackhouse.sh/offences"
    return fetch(url)
        .then((res) => res.json())
        .then((res) => res.offences)
}

function useOffsCategory() {
    const [loading, setLoading] = useState(true);
    const [types, setTypes] = useState([]);
    const [error, setError] = useState(null);
    useEffect(() => {
        OpenSource().then((types) => {
            setTypes(types);
            setLoading(false);
        }).catch((e) => {
            setError(e);
            setLoading(false);
        })
    }, []);

    OpenSource();
    return {
        loading,
        types,
        error,
    };
}
