import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
import ReactTable from "react-table";
import "react-table/react-table.css";

export function ShowOffences() {
    const { searching, offences, Serror } = useOffences();
    if (searching) {
        return <div className="loading"> Searching... </div>
    }

    if (Serror) {
        return <p>Something's wrong: {Serror.message}</p>
    }

    const columns = [{
        Header: "Offences List",
        accessor: '',
        Cell: row => (
            <div style={{ textAlign: "center" }}>{row.value}</div>),
    }]

    return (
        <div className="offence-table">
            <ReactTable
                data={offences}
                columns={columns}
                filterable
                defaultFilterMethod={(filter, row) => filterCaseInsensitive(filter, row)}
                // defaultPageSize={10}
                // pageSizeOptions={[10]}
                className="-short"
            />
            <div className="ocean">
                <div className="wave"></div>
                <div className="wave"></div>
            </div>
        </div>
    )
}

export function filterCaseInsensitive(filter, row) {
    const id = filter.pivotId || filter.id;
    return (
        row[id] !== undefined ?
            String(row[id].toLowerCase()).startsWith(filter.value.toLowerCase())
            :
            true
    );
}

function OpenSource() {
    const url = "https://cab230.hackhouse.sh/offences"
    return fetch(url)
        .then((res) => res.json())
        .then((res) => res.offences)

}

function useOffences() {
    const [searching, SetSearching] = useState(true);
    const [offences, SetOffence] = useState([]);
    const [Serror, setError] = useState(null);
    useEffect(() => {
        OpenSource().then((offences) => {
            SetOffence(offences);
            SetSearching(false);
        }).catch((e) => {
            setError(e);
            SetSearching(false);
        })
    }, []);

    return {
        searching,
        offences,
        Serror,
    };
}
