import React from "react";
import ReactDOM from "react-dom";
import {useState} from "react";
import Main from "./main";

let JWT = null;
export const RegAcc = (emailinput, passcodeinput) => {
    fetch("https://cab230.hackhouse.sh/register", {
        method: "POST",
        body: "email="+ emailinput + "&password=" + passcodeinput,
        headers: {
            "Content-type": "application/x-www-form-urlencoded"
        },
    })
        .then(function (response) {
            if (response.ok) {
                return response.json();
            }
            throw new Error("Network response was not ok");
        })
        .then(function (result) {
            let queryDiv = document.getElementById("query");
            queryDiv.innerHTML = JSON.stringify(result);
            RegAcc.disabled = true;

        })
        .catch(function (error) {
            console.log(
                "There has been a problem with your fetch operation: ",
                error.message
            );
        });
}

export const LoginAcc = (emailinput, passcodeinput)=> {
    fetch("https://cab230.hackhouse.sh/login", {
        method: "POST",
        body: "email="+ emailinput + "&password=" + passcodeinput,
        headers: {
            "Content-type": "application/x-www-form-urlencoded"
        },
        redirect: "follow",
    })
        .then(function (response) {
            if (response.ok) {
                return response.json();
            }
            throw new Error("Network response was not ok.");
        })

        .then(function (result) {
            let queryDiv = document.getElementById("query");
            queryDiv.innerHTML = JSON.stringify(result);
            JWT = result.token;

        })
        .catch(function (error) {
            console.log(
                "There has been a problem with your fetch operation: ",
                error.message
            );
        });
}
