import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
import {Dropdown} from 'react-bootstrap';


export function SearchBar(props) {

    const [innerSearch, SetInnerSearch] = useState("");

    return (
        <div>
            <input
                aria-labelledby="lga-button"
                name="lga"
                id="lga"
                type="lga"
                value={innerSearch}
                onChange={(e) => SetInnerSearch(e.target.value)}
            />
            <Dropdown>
            <Dropdown.Toggle variant="success" id="dropdown-basic">
                Dropdown Button
            </Dropdown.Toggle>
            </Dropdown>
            <button id="search-button" type="button" onClick={() => props.onSubmit(innerSearch)}>
                Search
            </button>
        </div>
    );
}

function GetSearchResult(itemsearch) {

    let key = sessionStorage.getItem("token");
    let getParam = { method: "GET" };
    let head = { Authorization: `Bearer ${key}` };
    getParam.headers = head;

    const baseUrl = "https://cab230.hackhouse.sh/search?";
    const query = "offence=" + itemsearch;
    const filter = "area=Moreton Bay Regional Council";

    let url = baseUrl + query + "&" + filter;
    return fetch(encodeURI(url), getParam)
        .then(res => res.json())
        .then(res => res.result)
        .then(result => result.map(off => ({
            LGA: off.LGA,
            total: off.total
        }))
        );
}

export function useSearch(itemsearch) {
    const [searching, SetSearching] = useState(true);
    const [offences, SetOffence] = useState([]);
    const [Serror, setError] = useState(null);

    // if (itemsearch === undefined){

    // }

    useEffect(() => {
        GetSearchResult(itemsearch).then(offences => {
            SetOffence(offences);
            SetSearching(false);
        }).catch((e) => {
            setError(e);
            SetSearching(false);
        });
    }, [itemsearch]);

    return {
        searching,
        offences,
        Serror,
    };
}
