import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
import JWT from "./api"


export function SearchBar(props) {

    const [innerSearch, SetInnerSearch] = useState("");

    return (
        <div>
            <input
                aria-labelledby="search-button"
                name="search"
                id="search"
                type="search"
                value={innerSearch}
                onChange={(e) => SetInnerSearch(e.target.value)}
            />
            <button id="search-button" type="button" onClick={() => props.onSubmit(innerSearch)}>
                Search
            </button>
        </div>
    );
}


function GetSearchResult(itemsearch) {

    let getParam = { method: "GET" };
    let head = { Authorization: `Bearer ${JWT}` };
    getParam.headers = head;

    const baseUrl = "https://cab230.hackhouse.sh/search?";
    const query = "offence=" + itemsearch;
    const filter = "area=Moreton Bay Regional Council";

    const url = baseUrl + query + "&" + filter;

    // const testurl = `https://newsapi.org/v2/top-headlines?country=us&apiKey=9b31b2cc112849a4b433d048f54a15f8&q=${itemsearch}`
    return fetch(encodeURI(url), getParam)
    // return fetch(testurl)
        .then(res => res.json())
        .then(res => res.articles)
        .then(articles => articles.map(result => ({
            LGA: result.LGA,
            total: result.total
            // title: result.title,
            // url: result.url
          }))
        );

}

export function useSearch(itemsearch) {
    const [searching, SetSearching] = useState(true);
    const [offences, SetOffence] = useState([]);
    const [Serror, setError] = useState(null);
    useEffect(() => {
        GetSearchResult(itemsearch).then(offences => {
            SetOffence(offences);
            SetSearching(false);
        }).catch((e) => {
            setError(e);
            SetSearching(false);
        });
    }, [itemsearch]);

    return {
        searching,
        offences,
        Serror,
    };
}
