import React from "react";
import {Bar, Line, Pie} from 'react-chartjs-2';

class Chart extends React.Component {
        constructor(props){
            super(props);
            this.state = {
                chartData:{

                }
            }
        }

        componentWillMount(){
            let lga = [];
            lga.push(this.props.map((area) => {
                area.lga
            }));
            let numbers = [];
            numbers.push(this.props.map((area) => {
                area.total
            }));
            this.setState({
                chartData:{
                    labels: lga,
                    datasets:{
                        label: 'LGA',
                        data:numbers,
                        // backgroundColor:[],
                    }
                }
            })
        }

        render(){
            return (
                <div className="graph">
                    <Bar 
                        data={this.state.chartData}

                        options={{
                            maintainAspectRatio: false
                        }}                    
                    />              
                </div>
            )
        }
}
export {Chart}
