import React from "react";
import ReactDOM from "react-dom";
import { useState } from "react";
import { SearchBar, useSearch } from "./search";

export function Main() {
    const [search, setsearch] = useState("");
    const { searching, offences, Serror } = useSearch(search);

    if (searching) {
        return <div className="loading"> Searching... </div>
    }

    if(search === ""){
        return <p>Invalid input</p>
    }

    // if (Serror) {
    //     return <p>Something's wrong: {Serror.message}</p>
    // }



    return (
        <div className="search-display">
            <div>
                <SearchBar onSubmit={setsearch} />

                {offences.map((offence) => (
                    <Displayingmulti key={offence.LGA} name={offence.LGA} number={offence.total} />
                ))}

            </div>
        </div>
    )
}

function Displayingmulti(props) {
    return (
        <div>
            <p>{props.name}</p>
            <p>{props.number}</p>
        </div>
    );
}
