  fetch("https://cab230.hackhouse.sh/login", {
    method: "POST",
    body: "email=n10176250%40qut.edu.au&password=fuckPWD12345~",
    headers: {
      "Content-type": "application/x-www-form-urlencoded"
    }
  })
    .then(function(response) {
      if (response.ok) {
        return response.json();
      }
      throw new Error("Network response was not ok.");
    })
    .then(function(result) {
      let queryDiv = document.getElementById("query");
      queryDiv.innerHTML = JSON.stringify(result);
      JWT = result.token;
    })
    .catch(function(error) {
      console.log(
        "There has been a problem with your fetch operation: ",
        error.message
      );
    });
}

    render () {
        return(
        <div className="inner-container">
            <div className="header">
              SIGN IN NOW
            </div>

            <div className="box">

                <div className="input-group">

                  <label htmlFor="email">Email</label>
                  <input type="text" name="email" className="login-input" placeholder="Your email address"/>
                </div>

                <div className="input-group">

                  <label htmlFor="pwd">Password</label>
                  <input type="text" name="pwd" className="login-input" placeholder="Password"/>
                </div>

            <button type="button" className="login-btn" onClick={this.LoginAcc}>Sign in</button>

            </div>


        </div>
        );
    }

}

