import React from "react";

class Search extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            offsearch: "Armed Robbery",
            areasearch: "Moreton Bay Regional Council",
            results: [],
        }
        this.changeOff = this.changeOff.bind(this);
        // this.changePass = this.changePass.bind(this);
    }

    changeOff(event) {
        this.setState({ offsearch: event.target.value });
    }

    componentDidMount() {
        let key = sessionStorage.getItem("token");
        let getParam = { method: "GET" };
        let head = { Authorization: `Bearer ${key}` };
        getParam.headers = head;

        const baseUrl = "https://cab230.hackhouse.sh/search?";
        const query = "offence=" + this.state.offsearch;
        const filterlga = "area=" + this.state.areasearch;
        // const filterage = "age=" + age;
        // const filtersex = "sex=" + sex;

        let url = baseUrl + query + "&" + filterlga;
        // + "&" + filterage + "&" + filtersex;
        fetch(encodeURI(url), getParam)
            .then(result => {
                return (result.json())
            })
            .then(data => {
                let results = data.result.map((off) => {
                    return ({
                        LGA: off.LGA,
                        total: off.total
                    })
                })
                for (var i in results) {
                    this.state.results.push(results[i]);
                }
                console.log(this.state.results);

            })
    }

    // Displayingmulti(props) {
    //     return (
    //         <div>
    //             <p>{props.name}</p>
    //             <p>{props.number}</p>
    //         </div>
    //     );
    // }
    displaying() {
        return (
        <ul>
            {this.state.results.map((result) => (
                <li key={result.LGA}>
                    Location: {result.LGA}, Total: {result.total}
                </li>
            ))}
        </ul>
        );
    }

    render() {

        return (
            <div>
                <input
                    aria-labelledby="offence-input"
                    name="offence"
                    id="offence"
                    type="offence"
                    placeholder="Search Offences"
                    value={this.state.offsearch}
                    onChange={this.changeOff}
                />
                <button id="search-button" type="button" onClick={displaying}>
                    Search
            </button>

            </div>

        );
    };

}

export { Search }
