import React from "react";
import ReactDOM from "react-dom";
import {useState, useEffect} from  "react";

export function Main () {
    const {loading,types,error} = useOffsCategory();

    if (loading){
        return <div className="loading"> Searching... </div>
    }

    if (error){
        return <p>Something's wrong: {error.message}</p>
    }
    return (
        <div className="all-offences">
            {types.map(type =>(
                <Displaying name={type.offs.map(off)} />
            ))}

        </div>

    )
}

function Displaying(props) {
    return (
      <div>
        <p>{props.name}</p>
      </div>
    );
  }

function OpenSource () {
    const url = "https://cab230.hackhouse.sh/offences"
    return fetch(url)
      .then(res => res.offs);
}

function useOffsCategory () {
    const [loading, setLoading] = useState(true);
    const [types, setTypes] = useState([]);
    const [error, setError] = useState(null);
    useEffect(() => {
        OpenSource().then(types => {
            setTypes(types);
            setLoading(false);
        }).catch((e) => {
            setError(e);
            setLoading(false);
        })
    },[]);

    OpenSource();
    return{
        loading,
        types,
        error,
    };
}
