import React, { component } from "react";
import ReactDOM from "react-dom";
import { BrowserRouter, Route, Link, Redirect } from "react-router-dom";
import { AuthenMain } from "./authen";
import { Main } from "./main";
import {SearchBar} from "./search";
import {useSearch} from "./search";
import {Displaying} from "./main";


class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      search: "",
    };
  }

  render() {
    const {searching, offences, error} = useSearch(search);
    return (

      <BrowserRouter>
        <div className="main-authen">
          <Link to="/authen">Authentication</Link>
        </div>

        <div className="main-page">
          <Link to="/main">Protected MainPage</Link>
        </div>
        <div>
          <SearchBar onSubmit={this.setState(search)}/>
        </div>
        <div>
          {offences.map((offence) =>(
                <Displaying key={offence} name={offence.LGA} />
            ))}
        </div>
        <Route path="/authen" component={AuthenMain} />
        <Route path="/main"  component={Main} />

      </BrowserRouter>

    )



  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
