import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
import { SearchBar, useSearch } from "./search";

export function Main() {
    const [search, setsearch] = useState("");
    // if (Serror) {
    //     return <p>Something's wrong: {Serror.message}</p>
    // }
    if (search !== "") {
        const { searching, offences, Serror } = useSearch(search);
        if (searching) {
            return <div className="loading"> Searching... </div>
        }
        return (
            <div className="search-display">
                <div>
                    <SearchBar onSubmit={setsearch} />

                    {offences.map((offence) => (
                        <Displayingmulti name={offence.LGA} number={offence.total} />
                    ))}
                </div>
            </div>
        )
    }
    else {
        const { searching, offences, Serror } = useSearch("");
        if (searching) {
            return <div className="loading"> Searching... </div>
        }
        return (
            <div className="search-display">
                <div>
                    <SearchBar onSubmit={setsearch} />

                    {offences.map((offence) => (
                        <Displaying key={offence} name={offence} />
                    ))}
                </div>
            </div>
        )
    }
}

function Displayingmulti(props) {
    return (
        <div>
            <p>{props.name}</p>
            <p>{props.number}</p>
        </div>
    );
}

function Displaying(props) {
    return (
        <div>
            <p>{props.name}</p>
        </div>
    );
}
