import React from "react";
import ReactDOM from "react-dom";
import { useState } from "react";
import { SearchBar, useSearch } from "./search";

export function Main() {
    const [search, setsearch] = useState("Armed Robbery");
    const [lgasearch, setlgasearch] = useState("Moreton Bay Regional Council");
    const [sexparam, setsexparam] = useState("Male");
    const [ageparam, setageparam] = useState("Adult");
    const { searching, offences, Serror } = useSearch(search,lgasearch,ageparam,sexparam);

    function states () {
        setsearch();
        setlgasearch();
        setsexparam();
        setageparam();
    }

    if (searching) {
        return <div className="loading"> Searching... </div>
    }

    if (Serror) {
        return <p>Something's wrong: {Serror.message}</p>
    }

    return (
        <div className="search-display">
            <div>
                <SearchBar onSubmit={states} />

                {offences.map((offence) => (
                    <Displayingmulti key={offence.LGA} name={offence.LGA} number={offence.total} />
                ))}

            </div>
        </div>
    )
}

function Displayingmulti(props) {
    return (
        <div>
            <p>{props.name}</p>
            <p>{props.number}</p>
        </div>
    );
}
