import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
import Main from "./main";
// import {setGlobal, useGlobal} from "react";

// setGlobal({
//     jwt: null
// });

let jwt = null;
export const RegAcc = (emailinput, passcodeinput) => {
    fetch("https://cab230.hackhouse.sh/register", {
        method: "POST",
        body: "email=" + emailinput + "&password=" + passcodeinput,
        headers: {
            "Content-type": "application/x-www-form-urlencoded"
        },
    })
        .then(function (response) {
            if (response.ok) {
                return response.json();
            }
            throw new Error("Network response was not ok");
        })
        .then(function (result) {
            let queryDiv = document.getElementById("query");
            queryDiv.innerHTML = JSON.stringify(result);
            RegAcc.disabled = true;

        })
        .catch(function (error) {
            console.log(
                "There has been a problem with your fetch operation: ",
                error.message
            );
        });
}

export const LoginAcc = (emailinput, passcodeinput) => {
    // const [jwt, setjwt] = useGlobal();
    fetch("https://cab230.hackhouse.sh/login", {
        method: "POST",
        body: "email=" + emailinput + "&password=" + passcodeinput,
        headers: {
            "Content-type": "application/x-www-form-urlencoded"
        },
        redirect: "follow",
    })
        .then(function (response) {
            if (response.ok) {
                return response.json();
            }
            throw new Error("Network response was not ok.");
        })

        .then(function (result) {
            let queryDiv = document.getElementById("query");
            queryDiv.innerHTML = JSON.stringify(result);
            jwt = result.token;
            sessionStorage.setItem("token", result.token);
            // SetToken(result.token);
        })
        .catch(function (error) {
            console.log(
                "There has been a problem with your fetch operation: ",
                error.message
            );
        });
}

export function SearchBar(props) {

    const [innerSearch, SetInnerSearch] = useState("");

    return (
        <div>
            <input
                aria-labelledby="search-button"
                name="search"
                id="search"
                type="search"
                value={innerSearch}
                onChange={(e) => SetInnerSearch(e.target.value)}
            />
            <button id="search-button" type="button" onClick={() => props.onSubmit(innerSearch)}>
                Search
            </button>
        </div>
    );
}


function GetSearchResult(itemsearch) {

    let key = sessionStorage.getItem("token");
    console.log(key);
    let getParam = { method: "GET" };
    let head = { Authorization: `Bearer ${key}` };
    getParam.headers = head;

    const baseUrl = "https://cab230.hackhouse.sh/search?";
    const query = "offence=" + itemsearch;
    const filter = "area=Moreton Bay Regional Council";

    const url = baseUrl + query + "&" + filter;

    // const testurl = `https://newsapi.org/v2/top-headlines?country=us&apiKey=9b31b2cc112849a4b433d048f54a15f8&q=${itemsearch}`
    return fetch(encodeURI(url), getParam)
    // return fetch(testurl)
        .then(res => console.log(res.json()))
        .then(res => console.log(res.result))
        .then(result => result.map(off => ({
            LGA: off.LGA,
            total: off.total
            // title: result.title,
            // url: result.url
          }))
        );

}

export function useSearch(itemsearch) {
    const [searching, SetSearching] = useState(true);
    const [offences, SetOffence] = useState([]);
    const [Serror, setError] = useState(null);
    useEffect(() => {
        GetSearchResult(itemsearch).then(offences => {
            SetOffence(offences);
            SetSearching(false);
        }).catch((e) => {
            setError(e);
            SetSearching(false);
        });
    }, [itemsearch]);
    console.log(offences);
    return {
        searching,
        offences,
        Serror,
    };

}
