import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";


export function SearchBar(props) {

    const [innerSearch, SetInnerSearch] = useState("");
    const [ageParam, SetAgeParam] = useState("");
    const [sexParam, SetSexParam] = useState("");

    return (
        <div>
            <input
                aria-labelledby="lga-button"
                name="lga"
                id="lga"
                type="lga"
                value={innerSearch}
                onChange={(e) => SetInnerSearch(e.target.value)}
            />

            {/* <select className="area-option" id="area" value={ageParam} onChange={(e) => SetAgeParam(e.target.value)}>
                <option value="Adult">Adult</option>
                <option value="Juvenile">Juvenile</option>
            </select> */}

            <select className="age-option" id="age" value={ageParam} onChange={(e) => SetAgeParam(e.target.value)}>
                <option value="Adult">Adult</option>
                <option value="Juvenile">Juvenile</option>
            </select>

            <select className="sex-option" id="sex" value={sexParam} onChange={(e) => SetSexParam(e.target.value)}>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
            </select>



            <button id="search-button" type="button" onClick={() => props.onSubmit(innerSearch)}>
                Search
            </button>
        </div>
    );
}

function GetSearchResult(itemsearch) {

    let key = sessionStorage.getItem("token");
    let getParam = { method: "GET" };
    let head = { Authorization: `Bearer ${key}` };
    getParam.headers = head;

    const baseUrl = "https://cab230.hackhouse.sh/search?";
    const query = "offence=" + itemsearch;
    const filter = "area=Moreton Bay Regional Council";

    let url = baseUrl + query + "&" + filter;
    return fetch(encodeURI(url), getParam)
        .then(res => res.json())
        .then(res => res.result)
        .then(result => result.map(off => ({
            LGA: off.LGA,
            total: off.total
        }))
        );
}

export function useSearch(itemsearch) {
    const [searching, SetSearching] = useState(true);
    const [offences, SetOffence] = useState([]);
    const [Serror, setError] = useState(null);

    // if (itemsearch === undefined){

    // }

    useEffect(() => {
        GetSearchResult(itemsearch).then(offences => {
            SetOffence(offences);
            SetSearching(false);
        }).catch((e) => {
            setError(e);
            SetSearching(false);
        });
    }, [itemsearch]);

    return {
        searching,
        offences,
        Serror,
    };
}
