import React from "react";
import {Bar, Line, Pie} from 'react-chartjs-2';

class Chart extends React.Component {
        constructor(props){
            super(props);
            this.state = {
                chartData:{

                }
            }
        }

        componentWillMount(){
            this.setState({
                chartData:{
                    labels:this.props.map((area) => {
                        area.lga
                    }),
                    datasets:[{
                        label: 'LGA',
                        data:this.props.map((area) => {
                            area.total
                        }),
                        // backgroundColor:[],
                    }]
                }
            })
        }

        render(){
            return (
                <div className="graph">
                    <Bar 
                        data={this.state.chartData}

                        options={{
                            maintainAspectRatio: false
                        }}                    
                    />              
                </div>
            )
        }
}
export {Chart}
