import React from "react";
import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
import ReactTable from "react-table";


export function ShowOffences() {
    const { searching, offences, Serror } = useOffences();
    if (searching) {
        return <div className="loading"> Searching... </div>
    }

    if (Serror) {
        return <p>Something's wrong: {Serror.message}</p>
    }

    // const columns = [{
    //     Header: "Offences List" ,
    //     accessor: 
    // }]


    return (
        <div className="search-display">
            <div>>
                <table>
                {offences.map((offence) => (
                        <Displaying key={offence} name={offence} />
                    ))}
                </table>
            </div>
        </div>

    )
}

function Displaying(props) {
    return (
        <div>
            <p>{props.name}</p>
        </div>
    );
}

function OpenSource() {
    const url = "https://cab230.hackhouse.sh/offences"
    return fetch(url)
        .then((res) => res.json())
        .then((res) => res.offences)

}

function useOffences() {
    const [searching, SetSearching] = useState(true);
    const [offences, SetOffence] = useState([]);
    const [Serror, setError] = useState(null);
    useEffect(() => {
        OpenSource().then((offences) => {
            SetOffence(offences);
            SetSearching(false);
        }).catch((e) => {
            setError(e);
            SetSearching(false);
        })
    }, []);

    return {
        searching,
        offences,
        Serror,
    };
}
